mapper.delete:
DELETE FROM
    azbrain_user_likes_article
WHERE
    article_id = #{articleId}
AND
    user_id = #{userId}
;
UPDATE
    azbrain_article
SET
    like_num = like_num - 1
WHERE
    id = #{articleId}
AND
    like_num > 0
;

mapper.select:
SELECT a.id AS id, a.title AS title, source, author, topic_id, t.title AS topicTitle, like_num, top_flag, favorite_num, a.create_time AS create_time FROM azbrain_article a INNER JOIN azbrain_topic t ON a.topic_id = t.id WHERE a.status = #{status}
AND top_flag = 1
AND a.title LIKE CONCAT('%',#{name},'%')
AND a.source = #{source}
AND a.create_time >= #{startTime}
AND a.create_time <= #{endTime}
ORDER BY create_time DESC

mapper.update:
UPDATE
    azbrain_article
SET
    status = #{status}
WHERE
    id = #{articleId}

UPDATE
    azbrain_article
SET
    clicked_num = clicked_num + #{clickedNum}
WHERE
    id = #{articleId}
