mapper.select:
SELECT l.id, l.user_id, u1.name as user_name, l.by_user_id, u2.name as by_user_name, l.way, l.mobile, l.meeting_pwd, l.meeting_host, l.cdate, l.start_time, l.end_time, l.user_evaluated, l.by_user_evaluated, l.create_time, l.status FROM azbrain_user_consultation_log l INNER JOIN azbrain_user u1 ON l.user_id = u1.id INNER JOIN azbrain_user u2 ON l.by_user_id = u2.id WHERE 1=1
AND l.status = #{status}
AND l.user_evaluated = #{userEvaluated}
AND l.by_user_evaluated = #{byUserEvaluated}
AND l.way = #{way}
AND (l.user_evaluated = 0 OR l.by_user_evaluated = 0) AND l.status != '98' AND l.status != '99'
AND u1.name LIKE CONCAT('%',#{userName},'%')
AND u2.name LIKE CONCAT('%',#{byUserName},'%')
ORDER BY create_time DESC
