mapper.update:
UPDATE
  azbrain_evaluate_label
SET
  status = #{status}
WHERE
  id = #{id}

mapper.select:
SELECT
FROM azbrain_evaluate_label WHERE classify = #{classify} AND status != '99' AND value_type = 'star'
AND name LIKE CONCAT('%',#{name},'%')
AND create_time >= #{startTime}
AND create_time <= #{endTime}
ORDER BY create_time DESC

SELECT id, `who`, content, `type`, `level`, event_id as eventId, duration, remark, create_time as createTime FROM azbrain_event_log WHERE `type` = #{type}
AND create_time >= #{startTime}
AND create_time <= #{endTime}
ORDER BY create_time DESC
