mapper.select:
SELECT id, name, gender, job_number, business_unit, photo, mobile, consult_num, consult_duration, likes_num, favorite_num, status, user_type, create_time FROM azbrain_user WHERE status != '99' AND 1 = 1
AND name LIKE CONCAT('%',#{name},'%')
AND create_time >= #{startTime}
AND create_time <= #{endTime}
AND job_number LIKE CONCAT('%',#{keycode},'%')
ORDER BY id DESC

SELECT * FROM azbrain_user
WHERE
    name = #{name}

mapper.update:
UPDATE
    azbrain_user
SET

    consulted_num = consulted_num + 1,
    consulted_duration = consulted_duration + #{duration,jdbcType=INTEGER}



    consult_num = consult_num + 1,
    consult_duration = consult_duration + #{duration,jdbcType=INTEGER}

WHERE
    id = #{userId,jdbcType=INTEGER}

UPDATE azbrain_user
SET
    status = #{status}
WHERE
    id = #{userId}

mapper.select:
SELECT id, `name`, gender, job_number, business_unit, photo, mobile, consulted_num, consulted_duration, once_consultation_duration, status, create_time FROM azbrain_user WHERE status != '99' AND user_type = '10'
AND name LIKE CONCAT('%',#{name},'%')
AND create_time >= #{startTime}
AND create_time <= #{endTime}
AND job_number LIKE CONCAT('%',#{keycode},'%')
ORDER BY id DESC

mapper.insert:
